package main

import (
	"crypto/md5"
	"flag"
	"fmt"
	"os"
)

var flagOut = flag.String("o", "a.out.exe", "set output file")

func main() {
	flag.Parse()
	if err := os.WriteFile(*flagOut, minimal_exe, 0777); err != nil {
		panic(err)
	}

	md5Sum := fmt.Sprintf("%x", md5.Sum(minimal_exe))
	if md5Sum != minimal_exe_md5 {
		panic("invalid md5 sum")
	}

	fmt.Printf("%x %s\n", md5Sum, *flagOut)
}

